t.target.type !== 'checkbox') {                    // $(':checkbox', this).trigger('click');                // }            // });						$("#Cabang").change(function(){                var Cabang=$("#Cabang").val();                var postdata = {'cabang':Cabang};										$("#ajaxLoader").show();                    var output = $('#returPicklist tbody');                    var link = "<?php echo base_url() ?>index.php/admin_picklist_retur/get_ajax_picklist";                    $.ajax({                            type: 'POST',                            url: link,                            dataType: 'jsonp',                            data: postdata,                            jsonp: 'jsoncallback',                            // timeout: 5000,                            success: function(data){                                var str='';                                $.each(data, function(i,item){								// alert(item['Jenis']);									if (item['Jenis'] == 'noTransaksi'){										var output = $('#noTransaksi').val(item['Kodenota']);									}else {										str+= '<tr onclick="checkthebox(this)"><td width="50px" style="text-align: center"><input type="checkbox" name="idKodeNota[]" value="'+item['NoPicklist']+'" /></td>';										str+= '<td  width="100px">'+item['Jenis']+'</td>';										str+= '<td  width="150px">'+item['Kodenota']+'</td>';										str+= '<td  width="100px">'+item['Tgl']+'</td>';										str+= '<td  width="250px">'+item['Perusahaan']+'</td>';										str+= '<td  width="125px">'+item['Gudang']+'</td>';										str+= '<td  width="50px">'+item['SKU']+'</td>';										str+= '<td  width="50px">'+item['Total']+'</td></tr>';									}                                });                                output.html(str); 								$("#ajaxLoader").hide();                            },                            // error: function (xhr, ajaxOptions, thrownError) {                                // alert(xhr.responseText);                                // alert(thrownError);                            // }                            /*error: function(){                                    //alert(data,null,'Error','Ok');                                    alert('There is a problem',null,'Error','Ok');                            }*/                    });                            });						            $(function() {                $.extend($.tablesorter.themes.bootstrap, {                    // these classes are added to the table. To see other table classes available,                    // look here: http://twitter.github.com/bootstrap/base-css.html#tables                    table      : 'table table-bordered',                    header     : 'bootstrap-header', // give the header a gradient background                    footerRow  : '',                    footerCells: '',                    icons      : '', // add "icon-white" to make them white; this icon class is added to the <i> in the header                    sortNone   : 'bootstrap-icon-unsorted',                    sortAsc    : 'icon-chevron-up',                    sortDesc   : 'icon-chevron-down',                    active     : '', // applied when column is sorted                    hover      : '', // use custom css here - bootstrap class may not override it                    filterRow  : '', // filter row class                    even       : '', // odd row zebra striping                    odd        : ''  // even row zebra striping                });                // call the tablesorter plugin and apply the uitheme widget                $("table").tablesorter({                    theme : "bootstrap", // this will                     widthFixed: true,                    headerTemplate : '{content} {icon}', // new in v2.7. Needed to add the bootstrap icon!                    // widget code contained in the jquery.tablesorter.widgets.js file                    // use the zebra stripe widget if you plan on hiding any rows (filter widget)                    widgets : [ "uitheme", "filter", "zebra" ],                    headers: {                        // 4: { sorter: false }                    },                    widgetOptions : {                        // using the default zebra striping class name, so it actually isn't included in the theme variable above                        // this is ONLY needed for bootstrap theming if you are using the filter widget, because rows are hidden                        zebra : ["even", "odd"],                        // reset filters button                        filter_reset : ".reset",                        filter_formatter : {                            // 4 : function(){return false;}                        }                        // set the uitheme widget to use the bootstrap theme class names                        // uitheme : "bootstrap"                    }                })                .tablesorterPager({                    // target the pager markup - see the HTML block below                    container: $(".pager"),                    // target the pager page select dropdown - choose a page                    cssGoto  : ".pagenum",                    // remove rows from the table to speed up the sort of large tables.                    // setting this to false, only hides the non-visible rows; needed if you plan to add/remove rows with the pager enabled.                    removeRows: false,                    // output string - default is '{page}/{totalPages}';                    // possible variables: {page}, {totalPages}, {filteredPages}, {startRow}, {endRow}, {filteredRows} and {totalRows}                    output: '{startRow} - {endRow} / {filteredRows} ({totalRows})'                });            });        </script>    </body></html>